mapper.select:
SELECT ACCT_ID , ACCT_NM , ACC_ROLE_CD , USE_YN , FR_RGST_DTTM , FR_RGST_ID , FINL_CHG_DTTM , FINL_CHG_ID FROM TB_CO1001M_ADMACCT
AND ACCT_ID = #{acctId}
AND ACCT_NM LIKE CONCAT('%', #{acctNm}, '%')
AND ACC_ROLE_CD = #{accRoleCd}
AND USE_YN = #{useYn}
ORDER BY FR_RGST_DTTM DESC

SELECT COUNT(1) > 0
  FROM TB_CO1001M_ADMACCT


                AND ACCT_ID = #{acctId}

mapper.insert:
INSERT INTO TB_CO1001M_ADMACCT
     ( ACCT_ID
     , ACCT_PWD
     , ACCT_NM
     , ACC_ROLE_CD
     , USE_YN
     , FR_RGST_DTTM
     , FR_RGST_ID
     , FINL_CHG_DTTM
     , FINL_CHG_ID
     )
VALUES (
       #{acctId}
     , #{acctPwd}
     , #{acctNm}
     , #{accRoleCd}
     , 'Y'
     , NOW()
     , #{frRgstId}
     , NOW()
     , #{finlChgId}
)

mapper.update:
UPDATE TB_CO1001M_ADMACCT
   SET ACCT_PWD = #{acctPwd}
     , ACCT_NM = #{acctNm}
     , ACC_ROLE_CD = #{accRoleCd}
     , USE_YN = #{useYn}
     , FINL_CHG_DTTM = NOW()
     , FINL_CHG_ID = #{finlChgId}
 WHERE ACCT_ID = #{acctId}
