mapper.select:
SELECT T.WORD_ID , T.WORD_NM , T.WORD_PSC_NM , T.WORD_LGC_NM , T.DESC_CTEN , T.USE_YN , T.FR_RGST_DTTM , T.FR_RGST_ID , T.FINL_CHG_DTTM , T.FINL_CHG_ID FROM TB_CO0001M_WORD T
AND T.WORD_ID = #{wordId}
AND T.WORD_NM LIKE CONCAT('%', #{wordNm}, '%')
AND T.WORD_PSC_NM LIKE CONCAT('%', #{wordPscNm}, '%')
AND T.WORD_LGC_NM LIKE CONCAT('%', #{wordLgcNm}, '%')
AND T.USE_YN = #{useYn}

SELECT COUNT(1) > 0
FROM TB_CO0001M_WORD


        WORD_ID = #{wordId}


        AND WORD_LGC_NM = #{wordLgcNm}


        AND WORD_PSC_NM = #{wordPscNm}

mapper.insert:
INSERT INTO TB_CO0001M_WORD
     ( WORD_ID
     , WORD_PSC_NM
     , WORD_LGC_NM
     , DESC_CTEN
     , USE_YN
     , FR_RGST_DTTM
     , FR_RGST_ID
     , FINL_CHG_DTTM
     , FINL_CHG_ID
     )
     VALUES
         ( #{wordId}
         , #{wordPscNm}
         , #{wordLgcNm}
         , #{descCten}
         , 'Y'
         , NOW()
         , #{frRgstId}
         , NOW()
         , #{finlChgId}
         )

mapper.update:
UPDATE TB_CO0001M_WORD
   SET WORD_PSC_NM   = #{wordPscNm}
     , WORD_LGC_NM   = #{wordLgcNm}
     , DESC_CTEN     = #{descCten}
     , USE_YN        = #{useYn}
     , FINL_CHG_DTTM = NOW()
     , FINL_CHG_ID   = #{finlChgId}
 WHERE WORD_ID = #{wordId}
